import React, { useEffect, useState, useContext } from "react"; // Importa useContext
import axios from "axios";
import Card from '../Components/Card';
import { ThemeContext } from '../ThemeContext';  // Asegúrate de que esta ruta sea la correcta

const Home = () => {
  
  const [dentists, setDentists] = useState([]);
  const { state } = useContext(ThemeContext);  // Usa el ThemeContext aquí

  // Actualiza la clase del body cuando el tema cambie
  useEffect(() => {
    document.body.className = state.theme === 'light' ? 'body-light-theme' : 'body-dark-theme';
  }, [state.theme]);

  useEffect(() => {
    axios.get("https://jsonplaceholder.typicode.com/users")
      .then(response => {
        setDentists(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the data", error);
      });
  }, []);
  
  return (
    <div className="card-grid">      
      {dentists.map(dentist => (
        <Card
          key={dentist.id}
          name={dentist.name}
          username={dentist.username}
          id={dentist.id}
        />
      ))}
    </div>
  );
};

export default Home;
